import {useEffect, useState} from 'react'

function Square({value, onSquareClick}) {

  return (
    <button
      className="square"
      onClick={onSquareClick}
    >
      {value}
    </button>
  );
}


export default function Board() {
  const [squares, setSquares] = useState(Array(9).fill(null))
  const [xIsNext, setXIsNext] = useState(true);



  const [counter, setCounter] = useState(0);
  const [isClicked, setClicked] = useState(false)
  const [squareTemp, setSquareTemp] = useState(0);
  const [squareMove, setSquareMove] = useState(null);

  useEffect(() => {
    console.log("Updated squares:", squares);
  }, [squares]);

  function handleClick(i) 
  {
    console.log("squares before:" + squares)

    if(squares[i] && !isClicked){return;}
    const nextSquares = squares.slice();
    let x = xIsNext ? "X" : "O"
    console.log("newsquares before:" + nextSquares)
    if(counter < 3)
    {
      if (xIsNext) {
        nextSquares[i] = "X";
      } else {
        nextSquares[i] = "O";
        setCounter(counter+1)
      }
      if(i == 4){setSquareMove = x;}
    } else {
      ;
      if(squares[i] === x && !isClicked)
      {
        setClicked(true);
        setSquareTemp(i);
        return;
      } else if (!squares[i] && isClicked){
        nextSquares[squareTemp] = null;
        nextSquares[i] = x;
        if((nextSquares[4] === x) && !calculateWinner(nextSquares))
        {
          nextSquares[i] = null;
          nextSquares[squareTemp] = x;
          setClicked(false);
          return;
        }

        setClicked(false);
      } else {
        return;
      }
    }

    setSquares(nextSquares);
    setXIsNext(!xIsNext);
    console.log("squares after:" + squares)
    console.log("newsquares after:" + nextSquares)
  }

  const winner = calculateWinner(squares);
  let status;
  if (winner) {
    status = "Winner: " + winner;
  } else {
    status = "Next player: " + (xIsNext ? "X" : "O");
  }

  return (
    <>
    <div className='status'>{status}</div>
    <div className="board-row">
      <Square value={squares[0]} onSquareClick={()=>{handleClick(0)}}/>
      <Square value={squares[1]} onSquareClick={()=>{handleClick(1)}}/>
      <Square value={squares[2]} onSquareClick={()=>{handleClick(2)}}/>
    </div>

    <div className="board-row">
      <Square value={squares[3]} onSquareClick={()=>{handleClick(3)}}/>
      <Square value={squares[4]} onSquareClick={()=>{handleClick(4)}}/>
      <Square value={squares[5]} onSquareClick={()=>{handleClick(5)}}/>
    </div>

    <div className="board-row">
      <Square value={squares[6]} onSquareClick={()=>{handleClick(6)}}/>
      <Square value={squares[7]} onSquareClick={()=>{handleClick(7)}}/>
      <Square value={squares[8]} onSquareClick={()=>{handleClick(8)}}/>
    </div>
    </>
  );

}

function calculateWinner(squares) {
  const lines = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6],
    [1, 4, 7],
    [2, 5, 8],
    [0, 4, 8],
    [2, 4, 6]
  ];
  for (let i = 0; i < lines.length; i++) {
    const [a, b, c] = lines[i];
    if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
      return squares[a];
    }
  }
  return null;
}
